<template>    <xzc-layout title="button">        <xzc-example title="按钮 large primary">            <xzc-button size="large">我是一个primary large</xzc-button>        </xzc-example>        <xzc-example title="按钮 warn size:default">            <xzc-button type="warn">我是一个</xzc-button>        </xzc-example>        <xzc-example title="按钮 圆角 danger size:default">            <xzc-button :circular="true" type="danger">我是一个</xzc-button>        </xzc-example>        <xzc-example title="按钮 圆角 small">            <xzc-button size="small" :circular="true" type="danger">small</xzc-button>            <xzc-button size="small" :circular="true" type="warn">small</xzc-button>            <xzc-button size="small" :circular="true" type="primary">small</xzc-button>        </xzc-example>        <xzc-example title="按钮 圆角 danger size:default disabled">            <xzc-button :disabled='true' :circular="true" type="danger">我是一个</xzc-button>        </xzc-example>        <xzc-example title="异步 按钮 圆角 danger size:default">            <xzc-button @click="asyncClick" :loading="loading" :circular="true" type="warn">我是一个异步</xzc-button>        </xzc-example>    </xzc-layout></template><script>    export default {        data(){            return{                loading:false            }        },        methods:{            asyncClick(){                this.loading = true;                let timer = setTimeout(()=>{                    console.log(this.$zIndex);                    clearTimeout(timer)                },1000)            }        }    }</script><style lang="scss">   @import "./index.scss";</style>