<template>    <xzc-layout title="confirm">        <xzc-example title="dialog 正常">            <xzc-button type="warn" @click="show">show</xzc-button>        </xzc-example>        <xzc-example title="dialog icon">            <xzc-button type="danger" @click="show1">show</xzc-button>        </xzc-example>        <xzc-example title="dialog 3个">            <xzc-button @click="show2">show</xzc-button>        </xzc-example>    </xzc-layout></template><script>    export default {        data(){            return{            }        },        methods:{            show(){                this.$dialog.show({                    title:'标题',                    msg:'我是内容我是内容我是内容我是内容',                    confirm:{                        cb:()=>{this.$tips.show('好的')}                    }                });            },            show1(){                this.$dialog.show({                    icon:'star',                    msg:'我是内容我是内容我是内容我是内容',                    confirm:{                        cb:()=>{this.$tips.show('好的')},                        text:'go'                    },                    cancel:{                        cb:()=>{this.$tips.show('cancel')},                        text:'cancel'                    }                });            },            show2(){                this.$dialog.show({                    icon:'star',                    msg:'我是内容我是内容我是内容我是内容',                    confirm:{                        cb:()=>{this.$tips.show('好的')},                        text:'go'                    },                    hesitate:{                        cb:()=>{this.$tips.show('算了...')},                        text:'noIdea'                    },                    cancel:{                        cb:()=>{this.$tips.show('cancel')},                        text:'cancel'                    }                });            }        }    }</script><style lang="scss">   @import "./index.scss";</style>