<template>    <xzc-layout>        <div slot="top" class="logo">            <img :src="logo" />        </div>        <div class="content">            <div class="block" v-for="(item,key) in menuList" :key="key">                <div @click="showChild(key)" class="blockTitle">{{item.title}}</div>                <ul v-show="item.opened">                    <li v-for="(i,dex) in item.child" :key="dex">                        <router-link :to="i.link">                            <xzc-icon icon="star"></xzc-icon>                            <span>{{i.title}}</span>                            <xzc-icon class="arrow" icon="arrow_right"></xzc-icon>                        </router-link>                    </li>                </ul>            </div>        </div>    </xzc-layout></template><script>    import menuList from './menuList'    export default {        data(){            return{                logo:require('../../assets/logo.png'),                menuList:menuList            }        },        methods:{            showChild(index){                let {menuList} = this;                menuList[index].opened = !menuList[index].opened;                this.menuList = menuList;            }        }    }</script><style lang="scss">    @import "./index.scss";</style>