<template>    <xzc-layout title="loading">        <xzc-example title="loading 默认">            <xzc-button size="small" type="warn" @click="show">show</xzc-button>            <xzc-button size="small" type="danger" @click="hide">hide</xzc-button>        </xzc-example>        <xzc-example title="loading 自定义">            <xzc-button size="small" type="warn" @click="show('请稍等...')">show</xzc-button>            <xzc-button size="small" type="danger" @click="hide">hide</xzc-button>        </xzc-example>    </xzc-layout></template><script>    export default {        data(){            return{            }        },        methods:{            show(msg){                this.$loading.show(msg);            },            hide(){                this.$loading.hide();            }        }    }</script><style lang="scss">   @import "./index.scss";</style>