<template>    <xzc-layout title="mask">        <xzc-example title="mask 通过组件v-model实现双向绑定">            <xzc-button @click="toShow">show</xzc-button>            <xzc-mask v-model="show"></xzc-mask>        </xzc-example>        <xzc-example title="mask locked=true">            <xzc-button type="warn" @click="toShow1">show</xzc-button>            <xzc-mask v-model="show1" :locked="true"></xzc-mask>        </xzc-example>    </xzc-layout></template><script>    export default {        data(){            return{                show:false,                show1:false,            }        },        methods:{            toShow(){                this.show = true;            },            toShow1(){                this.show1 = true;            }        }    }</script><style lang="scss">   @import "./index.scss";</style>