<template>    <xzc-layout title="popup">        <xzc-example title="popup 上">            <xzc-button @click="toShow(1)">show</xzc-button>            <xzc-popup v-model="show1" align="top">                <div class="contentTop">                    <xzc-button type="warn" @click="hide(1)">点我关闭</xzc-button>                </div>            </xzc-popup>        </xzc-example>        <xzc-example title="popup 左">            <xzc-button type="warn" @click="toShow(2)">show</xzc-button>            <xzc-popup v-model="show2" align="left">                <div class="contentLeft">                    <xzc-button type="danger" size="small" @click="hide(2)">点我关闭</xzc-button>                </div>            </xzc-popup>        </xzc-example>        <xzc-example title="popup 下">            <xzc-button @click="toShow(3)">show</xzc-button>            <xzc-popup v-model="show3" align="bottom">                <div class="contentBottom">                    <xzc-button type="warn" @click="hide(3)">点我关闭</xzc-button>                </div>            </xzc-popup>        </xzc-example>        <xzc-example title="popup 右">            <xzc-button type="warn" @click="toShow(4)">show</xzc-button>            <xzc-popup v-model="show4" align="right">                <div class="contentRight">                    <xzc-button type="danger" size="small" @click="hide(4)">点我关闭</xzc-button>                </div>            </xzc-popup>        </xzc-example>        <xzc-example title="popup 中间">            <xzc-button type="danger" @click="toShow(5)">show</xzc-button>            <xzc-popup v-model="show5" align="center">                <div class="contentCenter">                    <xzc-button size="small" @click="hide(5)">点我关闭</xzc-button>                </div>            </xzc-popup>        </xzc-example>    </xzc-layout></template><script>    export default {        data(){            return{                show1:false,                show2:false,                show3:false,                show4:false,                show5:false            }        },        methods:{            toShow(index){                this[`show${index}`] = true;            },            hide(index){                this[`show${index}`] = false;            }        }    }</script><style lang="scss">   @import "./index.scss";</style>