<template>    <xzc-layout title="toast">        <xzc-example title="toast 1500ms">            <xzc-button type="warn" @click="show">show</xzc-button>        </xzc-example>        <xzc-example title="toast 3000ms error">            <xzc-button type="danger" @click="show1">show</xzc-button>        </xzc-example>        <xzc-example title="toast success">            <xzc-button  @click="show2">show</xzc-button>        </xzc-example>    </xzc-layout></template><script>    export default {        data(){            return{            }        },        methods:{            show(){                this.$toast.show('我是一个提示');            },            show1(){                this.$toast.show({                    msg:'我是一个提示',                    delay:3000,                    type:'error'                });            },            show2(){                this.$toast.show({                    msg:'我是一个提示',                    type:'success',                    delay:300000,                });            },        }    }</script><style lang="scss">   @import "./index.scss";</style>