<template>    <xzc-layout title="topTips">        <xzc-example title="topTips icon 1500ms">            <xzc-button type="warn" @click="show">show</xzc-button>        </xzc-example>        <xzc-example title="topTips 3000ms warn">            <xzc-button type="danger" @click="show1">show</xzc-button>        </xzc-example>        <xzc-example title="topTips success">            <xzc-button  @click="show2">show</xzc-button>        </xzc-example>    </xzc-layout></template><script>    export default {        data(){            return{            }        },        methods:{            show(){                this.$tips.show({                    msg:'我是一个提示',                    icon:'smile'                });            },            show1(){                this.$tips.show({                    msg:'我是一个提示',                    delay:3000,                    type:'warn'                });            },            show2(){                this.$tips.show({                    msg:'我是一个提示',                    type:'success'                });            },        }    }</script><style lang="scss">   @import "./index.scss";</style>