<template>    <button        :type="actionType"        :size="size"        :disabled="disabled"        @click="onClick"        :class="'btn '+_type+' '+_size+' '+_disabled+' '+_circular"    >        <i v-show="loading" class="btn_loading"></i>        <slot></slot>    </button></template><script>    export default {        name: 'xzc-button',        props: {            actionType:{                type:[String],                default:'button'            },            type:{                type:[String],                default:'primary'            },            size:{                type:[String],                default:'default'            },            circular:{                type:[Boolean],                default:false            },            disabled:{                type:[Boolean],                default:false            },            loading:{                type:[Boolean],                default:false            }        },        computed:{            _type(){                if(!!this.type){                    return 'btn_'+this.type                }                return ''            },            _size(){                if(!!this.size){                    return 'btn_'+this.size                }                return ''            },            _disabled(){                if(this.disabled){                    return 'btn_disabled'                }                return ''            },            _circular(){                if(this.circular){                    return 'btn_circular'                }                return ''            }        },        methods:{            onClick(){                if(this.loading){                    return false                }                this.$emit('click')            }        }    }</script><style lang="scss">  @import "./index.scss";</style>