<template>    <div>        <xzc-mark v-model="show" :locked="true"></xzc-mark>        <div class="dialog">            <div v-show="!!title" class="dialogHeader">{{title}}</div>            <div class="body">                <xzc-icon v-show="!!icon" :icon="icon"></xzc-icon>                <span class="msg">{{msg}}</span>            </div>            <div class="footer">                <a class="dialogBtn cancel" href="javascript:;" @click="toCancel">{{cancelText||'取消'}}</a>                <a v-show="hasHesitate" class="dialogBtn hesitate" href="javascript:;" @click="toHesitate">{{hesitateText||'犹豫一下'}}</a>                <a class="dialogBtn confirmBtn" href="javascript:;" @click="toConfirm">{{confirmText||'确定'}}</a>            </div>        </div>    </div></template><script>    import Icon from '../../icon/index.vue'    import Mark from '../../mask/index.vue'    export default {        components:{            'xzc-icon':Icon,            'xzc-mark':Mark,        },        props:{            icon:{                type:[String],                default:''            },            msg:{                type:[String],                default:''            },            title:[String],            cancelText:[String],            hesitateText:[String],            confirmText:[String],            show:{                type:[Boolean],                default:false            },            hasHesitate:{                type:[Boolean],                default:false            },            toCancel:{                type:[Function],                default:()=>{}            },            toHesitate:{                type:[Function],                default:()=>{}            },            toConfirm:{                type:[Function],                default:()=>{}            }        },        methods:{            close(){            }        },        computed:{            classes(){                return this.show?'show':'hide'            }        }    }</script><style lang="scss">  @import "index";</style>