<template>    <div class="input">        <template v-if="1">            <input ref="input" type="text">        </template>        <template v-else="2">            <input ref="input" type="text">        </template>    </div></template><script>    export default {        name: 'xzc-input',        props: {            actionType:{                type:[String],                default:'button'            },            type:{                type:[String],                default:'primary'            },            size:{                type:[String],                default:'default'            },            circular:{                type:[Boolean],                default:false            },            disabled:{                type:[Boolean],                default:false            },            loading:{                type:[Boolean],                default:false            }        },        computed:{            _type(){                if(!!this.type){                    return 'btn_'+this.type                }                return ''            },            _size(){                if(!!this.size){                    return 'btn_'+this.size                }                return ''            },            _disabled(){                if(this.disabled){                    return 'btn_disabled'                }                return ''            },            _circular(){                if(this.circular){                    return 'btn_circular'                }                return ''            },            _loading(){                if(this.loading){                    return 'loading'                }                return ''            }        },        methods:{            onClick(){                if(this.loading){                    return false                }                this.$emit('click')            }        }    }</script><style lang="scss">  @import "./index.scss";</style>