<template>    <section class="layout">        <!--导航-->        <slot name="navbar">            <xzc-navbar v-show="!!title">                <a @click="back" class="headerLeft" slot="left">                    <xzc-icon class="backArrow" icon="arrow_left"></xzc-icon>                </a>                <div class="headerMid" slot="mid">{{title || ''}}</div>            </xzc-navbar>        </slot>        <slot name="top"></slot>        <!--内容-->        <section class="scrollView" ref="scrollView">            <slot></slot>        </section>        <!--底部-->        <slot name="footer"></slot>        <!--底部导航-->        <slot name="footerNavbar"></slot>    </section></template><script>    export default {        name: 'xzc-layout',        props: {            title: String        },        methods:{            back(){                window.history.back();            }        }    }</script><style lang="scss">  @import "./index.scss";</style>