<template>    <div v-show="myShow" @click.stop="hide" class="mask">        <slot></slot>    </div></template><script>    import {isIos,getScrollView,addClass,removeClass} from 'utils'    export default {        name: 'xzc-mask',        data(){            return{                myShow:false            }        },        props:{            locked:{                type:[Boolean],                default:false            },            value:{                type:[Boolean],                default:false            }        },        methods:{            hide(){                if(this.locked){                    return false;                }                this.myShow = false;                this.$emit('input',false)            }        },        watch:{            value(val){                this.myShow = val;                if(isIos()){                    if(val){                        addClass(this.scrollView,'fixIosScrollBug');                    }else {                        let timer = setTimeout(()=>{                            removeClass(this.scrollView,'fixIosScrollBug')                        },100);                        clearTimeout(timer);                    }                }            }        },        mounted(){            this.scrollView = getScrollView(this.$el)        },        destroyed(){            isIos()&&removeClass(this.scrollView,'fixIosScrollBug')        }    }</script><style lang="scss">  @import "./index.scss";</style>