<template>    <div v-show="myShow" @click.stop="hide" :style="'z-index:'+zIndex" class="mask">        <slot></slot>    </div></template><script>    export default {        name: 'xzc-mask',        data(){            return{                zIndex:this.$zIndex,                myShow:false            }        },        props:{            locked:{                type:[Boolean],                default:false            },            value:{                type:[Boolean],                default:false            }        },        methods:{            hide(){                if(this.locked){                    return false;                }                this.myShow = false;                this.$emit('input',false)            }        },        watch:{            value(val){                this.myShow = val;            }        },        created(){            if(this.$zIndex){                this.$zIndex++;                this.zIndex = this.$zIndex;            }        }    }</script><style lang="scss">  @import "./index.scss";</style>