<template>    <div>        <xzc-mask v-model="showMask" :locked="locked"></xzc-mask>        <div :class="classes">            <slot></slot>        </div>    </div></template><script>    import Mask from '../mask/index.vue'    export default {        name: 'xzc-popup',        components:{            'xzc-mask':Mask        },        data(){            return{                showMask:false            }        },        props:{            align:{                type:[String],                default:'bottom'            },            value:{                type:[Boolean],                default:false            },            locked:{                type:[Boolean],                default:true            }        },        computed:{            classes(){                return `popup ${this.align} ${this.value?this.align==='center'?'showCenter':'show':''}`            }        },        methods:{        },        watch:{            value(val){                this.showMask = val;            },            showMask(val){                this.$emit('input',val)            }        }    }</script><style lang="scss" scoped>  @import "./index.scss";</style>