<template>    <div :class="'toast '+classes">        <xzc-icon v-show="!!icon" :icon="icon"></xzc-icon>        <span>{{msg}}</span>    </div></template><script>    import Icon from '../../icon/index.vue'    export default {        components:{            'xzc-icon':Icon        },        props:{            msg:{                type:[String],                default:'undefined'            },            show:{                type:[Boolean],                default:false            },            type:{                type:[String],                default:''            }        },        methods:{        },        computed:{            classes(){                return this.show?'show':'hide'            },            icon(){                return !!this.type?this.type ==='error'?'error':'perfect':''            }        }    }</script><style lang="scss" scoped>  @import "index";</style>