<template>    <div :class="'topTips '+classes">        <xzc-icon :icon="icon" v-show="!!icon"></xzc-icon>        <span>{{text}}</span>    </div></template><script>    export default {        props:{            icon:{                type:[String]            },            text:{                type:[String],                default:''            }        },        computed:{            classes(){                if(!!icon){                    return 'contentLeft'                }                return 'contentCenter'            }        }    }</script><style lang="scss">  @import "./index.scss";</style>